grid vegetation_cell width: 50 height: 50 neighbors: 4 {
	float max_food <- 1.0;
	float food_prod <- rnd(0.01);
	float food <- rnd(1.0) max: max_food update: food + food_prod;
	rgb color <- rgb(int(255 * (1 - food)), 255, int(255 * (1 - food))) update: rgb(int(255 * (1 - food)), 255, int(255 * (1 - food)));
	list<vegetation_cell> neighbors2  <- (self neighbors_at 2);
}

grid water_cell width: 40 height: 40 neighbors: 8 {
    float water_level <- 1.0;
    float evaporation_rate <- rnd(0.01);
    float rainfall <- rnd(1.0) max: water_level update: water_level - evaporation_rate + rainfall;
    rgb color <- rgb(int(50 * (1 - water_level)), int(100 + 155 * water_level), int(50 * (1 - water_level))) update: rgb(int(50 * (1 - water_level)), int(100 + 155 * water_level), int(50 * (1 - water_level)));
    list<water_cell> nearby_water_sources <- (self neighbors_at 1) if (each.water_level > 0.8);
}

grid air_quality_cell width: 45 height: 45 neighbors: 8 {
    float pollutant_concentration <- rnd(0.1);
    float pollutant_dispersion <- rnd(0.01);
    pollutant_concentration <- pollutant_concentration - pollutant_dispersion;
    rgb color <- rgb(int(255 * (1 - pollutant_concentration)), int(255 * pollutant_concentration), int(255 * (1 - pollutant_concentration)));
    list<air_quality_cell> polluted_neighbors <- (self neighbors_at 1) if (each.pollutant_concentration > 0.5);
}

